INSERT INTO products (name, short_description, full_description, materials, production_time, price, discount, discounted_price, image, category_id) VALUES
('Памятник "Классик"', 'Классический памятник из черного гранита с золотой гравировкой', 'Элегантный памятник в классическом стиле из высококачественного черного гранита. Включает золотую гравировку имени и дат, а также декоративные элементы. Подходит для любого типа захоронения.', 'Черный гранит, золотая фольга', '7-10 дней', 1800.00, 10, 1620.00, '/images/singleMonument.jpg', 1),
('Памятник "Семейный"', 'Двойной памятник для семейных захоронений', 'Просторный двойной памятник из серого гранита с возможностью размещения двух имен. Включает общую эпитафию и декоративные элементы. Идеально подходит для семейных захоронений.', 'Серый гранит, бронза', '10-14 дней', 2800.00, 15, 2380.00, '/images/doubleMonument.jpg', 2),
('Мемориал "Вечность"', 'Монументальный мемориальный комплекс', 'Величественный мемориальный комплекс из красного гранита с декоративными колоннами и барельефами. Включает постамент, основную стелу и декоративные элементы. Создан для увековечивания памяти особо важных людей.', 'Красный гранит, мрамор, бронза', '21-30 дней', 4500.00, 20, 3600.00, '/images/memorialMonument.jpg', 3),
('Плита "Простота"', 'Простая гранитная плита для скромных захоронений', 'Минималистичная гранитная плита из серого гранита. Чистые линии и простота дизайна подчеркивают достоинство и скромность. Подходит для тех, кто предпочитает простоту и элегантность.', 'Серый гранит', '3-5 дней', 900.00, 5, 855.00, '/images/graniteSlab.jpg', 4),
('Надгробие "Традиция"', 'Традиционное надгробие с религиозными символами', 'Традиционное надгробие из белого мрамора с выгравированными религиозными символами. Включает крест и молитвенную надпись. Создано в соответствии с православными традициями.', 'Белый мрамор, золотая фольга', '5-7 дней', 1400.00, NULL, NULL, '/images/gravestone.jpg', 5);
